int main(int argc, const char *argv[])
{
	int cases, start, end, diff, low, mid, high, tmp, total, n;
	/* static + dynamic */
	for (n = 4001; n < SIZE; n++) {
		steps[n] = n * n;
	}
	scanf("%d", &cases);
next:
	while (cases--) {
		scanf("%d%d", &start, &end);
		diff = end - start;
		if (!diff) {puts("0"); continue;}
		low = 0, high = SIZE;
		/* bsearch */
		while (low <= high) {
			mid = (low + high) >> 1;
			tmp = steps[mid];
			if      (tmp < diff) low  = mid + 1;
			else if (tmp > diff) high = mid - 1;
			/* occasionally diff == n^2 */
			else {printf("%d\n", (mid << 1) - 1); goto next;}
		}
		/* diff < n^2 
		 * n = n - 1 */
		if (diff > tmp) {
			n = mid - 1; 
			diff -= steps[n];
		/* n^2 < diff */
		} else {
			n = mid;
			diff -= tmp;
		}
		/* steps == 2*n-1 */
		total = (n << 1) - 1;
		for ( ; diff; n--) {
			total += diff / n;
			diff %= n;
		}
		printf("%d\n", total);
	}
	return 0;
}
